package com.demo.rest.dbinterface;public class Queries {		public static final String insertDemoTableData = ""			+ "INSERT INTO `demoschema`.`demo_table` " + 			"(`policyID`," + 			"`statecode`," + 			"`county`," + 			"`construction`, " + 			"`notes`) " + 			"VALUE ";		public static final String insertGeoTableData = "INSERT INTO `demoschema`.`demo_table_geo_data` " + 			"(`policyID`, " + 			"`point_latitude`, " + 			"`point_longitude`) " + 			"VALUE ";			public static final String querySelectAll = ""			+ "SELECT base.policyID, " + 			"    base.statecode, " + 			"    base.county, " + 			"    base.construction, " + 			"    base.notes, " +			"    geo.point_latitude, " + 			"    geo.point_longitude " + 						"FROM `demoschema`.`demo_table` as base " + 			"JOIN `demoschema`.`demo_table_geo_data` as geo " + 			"ON base.policyID = geo.policyID " +			"limit 100;";		public static final String querySelectEntry = ""			+ "SELECT base.policyID, " + 			"    base.statecode, " + 			"    base.county, " + 			"    base.construction, " + 			"    base.notes, " + 			"    geo.point_latitude, " + 			"    geo.point_longitude " + 			"FROM `demoschema`.`demo_table` as base " + 			"JOIN `demoschema`.`demo_table_geo_data` as geo " + 			"ON base.policyID = geo.policyID " + 			"where geo.policyID in (?);";		public static final String queryUpdateSingleDemoEntry = ""			+ "UPDATE `demoschema`.`demo_table` " + 			"SET " + 			"`statecode` = ?, " + 			"`county` = ?, " + 			"`construction` = ?, " + 			"`notes` = ? " +			"WHERE `policyID` = ?;";		public static final String queryUpdateSingleGeoEntry = ""			+ "UPDATE `demoschema`.`demo_table_geo_data` " + 			"SET " + 			"`point_latitude` = ?, " + 			"`point_longitude` = ? " + 			"WHERE `policyID` = ?;";		public static final String queryDeleteSingleDemoEntry = ""			+ "DELETE FROM `demoschema`.`demo_table` " + 			"WHERE `policyID` = ?;";		public static final String queryDeleteSingleGeoEntry = ""			+ "DELETE FROM `demoschema`.`demo_table_geo_data` " + 			"WHERE `policyID` = ?;";		public static final String queryClearTableData = ""			+ "truncate demoschema.demo_table;"			+ "truncate demoschema.demo_table_geo_data;";}